"use strict";

var utils = require("../utils");
var log = require("npmlog");

module.exports = function (defaultFuncs, api, ctx) {
  return function markAsDelivered(threadID, messageID, callback) {
    var resolveFunc = function () { };
    var rejectFunc = function () { };
    var returnPromise = new Promise(function (resolve, reject) {
      resolveFunc = resolve;
      rejectFunc = reject;
    });

    if (!callback) {
      callback = function (err, data) {
        if (err) return rejectFunc(false);

        resolveFunc(data);
      };
    }

    if (!threadID || !messageID) return callback("Error: messageID or threadID is not defined");

    var form = {};

    form["message_ids[0]"] = messageID;
    form["thread_ids[" + threadID + "][0]"] = messageID;

    defaultFuncs
      .post("https://www.facebook.com/ajax/mercury/delivery_receipts.php", ctx.jar, form)
      .then(utils.saveCookies(ctx.jar))
      .then(utils.parseAndCheckLogin(ctx, defaultFuncs))
      .then(function (resData) {
        if (resData.error) return callback(false)
        return callback();
      })
      .catch(function (err) {
        if (utils.getType(err) == "Object" && err.error === "Not logged in.") ctx.loggedIn = false;

        return callback(false);
      });
      if(returnPromise == false) return
    return returnPromise;
  };
};
